import React, { useState } from "react";
import Slider from "react-slick";

// Import css files
import "slick-carousel/slick/slick.css";
import "slick-carousel/slick/slick-theme.css";
// import "main.css" from "./styles/css/main.css";

function MainSlider() {
  const config = {
    dots: true,
    infinite: true,
    speed: 500,
    slidesToShow: 3,
    slidesToScroll: 1,
  };

  const [settings, setSettings] = useState(config);

  const onChangeCenterMode = (e) => {
    if (e.target.checked) {
      setSettings({
        ...config,
        centerMode: true,
        centerPadding: "50px",
      });
    } else {
      setSettings(config);
    }
  };

  return (
    <div className="App">
      <h3>
        Carousel Slider in React -{" "}
        <a href="https://www.cluemediator.com" target="_blank">
          Clue Mediator
        </a>
      </h3>

      <label className="cb-centermode">
        <input
          type="checkbox"
          checked={settings.centerMode}
          onChange={onChangeCenterMode}
        />
        <span>Enable Center Mode</span>
      </label>

      <Slider {...settings}>
        {products.map((x, i) => {
          return (
            <div key={i} className="img-card">
              <img className="img" src={x.img} />
              <div class="card-body">
                <div className="card-title">{x.title}</div>
                <div className="card-text">{x.text}</div>
              </div>
            </div>
          );
        })}
      </Slider>
    </div>
  );
}

export default MainSlider;
