import React, { useState } from "react";
import Slider from "react-slick";

// Import css files
import "slick-carousel/slick/slick.css";
import "slick-carousel/slick/slick-theme.css";
// import "main.css" from "./styles/css/main.css";

function MainSlider() {
  const config = {
    dots: true,
    infinite: true,
    speed: 500,
    slidesToShow: 3,
    slidesToScroll: 1,
  };

  const [settings, setSettings] = useState(config);

  const onChangeCenterMode = (e) => {
    if (e.target.checked) {
      setSettings({
        ...config,
        centerMode: true,
        centerPadding: "50px",
      });
    } else {
      setSettings(config);
    }
  };

