import React, { useState } from "react";
import Slider from "react-slick";
// import SliderData from "./SliderData";

// Import css files
import "slick-carousel/slick/slick.css";
import "slick-carousel/slick/slick-theme.css";
// import "main.css" from "./styles/css/main.css";

function MainSlider() {
  const config = {
    dots: true,
    infinite: true,
    speed: 500,
    slidesToShow: 3,
    slidesToScroll: 1,
  };

  const [settings, setSettings] = useState(config);

  const products = [
    {
      img: "/images/product1.jpg",
      title: "Dolore magna",
      text: "Lorem ipsum dolor sit amet elit.",
    },
    {
      img: "/images/product2.jpg",
      title: "Eget est lorem",
      text: "Lorem Ipsum adipiscing elit ipsum.",
    },
    {
      img: "/images/product3.jpg",
      title: "Tempus imperdiet",
      text: "Orci porta non pulvinar neque laoreet.",
    },
    {
      img: "/images/product4.jpg",
      title: "Mattis rhoncus",
      text: "Bibendum neque egestas congue quisque.",
    },
    {
      img: "/images/product5.jpg",
      title: "Odio ut enim",
      text: "Mattis rhoncus urna neque viverra justo.",
    },
  ];

  return (
    <div className="App">
      <Slider {...settings}>
        {products.map((x, i) => {
          return (
            <div key={i} className="img-card">
              <img className="img" src={x.img} alt="" />
              <div class="card-body">
                <div className="card-title">{x.title}</div>
                <div className="card-text">{x.text}</div>
              </div>
            </div>
          );
        })}
      </Slider>
    </div>
  );
}
